	0.5
	#{= curDate }
	daily


	#{= siteurl }/guestbook.html
	0.5
	#{= curDate }
	daily

#{ for i, post in ipairs(blogposts) do }

	#{= siteurl }#{= getPostURL(post) }
	0.5
	#{= curDate }
	daily

#{ end }
#{ for tagname, tag in pairs(tags) do }

	#{= siteurl }#{= getTagURL(tag) }
	0.5
	#{= curDate }
	daily

#{ end }
#{ for catename, v in pairs(categories) do }

	#{= siteurl }#{= getCategoryURL(catename) }
	0.5
	#{= curDate }
	daily

#{ end }
